import React, { useState } from 'react';
import UricaQuestions from '../data/questions';

const Questionnaire = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState(new Array(32).fill(null));
  const [isComplete, setIsComplete] = useState(false);

  const handleAnswer = (value) => {
    const newAnswers = [...answers];
    newAnswers[currentQuestion] = value;
    setAnswers(newAnswers);
  };

  const handleNext = () => {
    if (currentQuestion < 31) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setIsComplete(true);
    }
  };

  if (isComplete) {
    return (
      <div className="max-w-2xl mx-auto p-6">
        <h2 className="text-2xl font-bold mb-4">Assessment Complete!</h2>
        <p>Thank you for completing the questionnaire.</p>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto p-6">
      <div className="mb-4">
        <span className="text-gray-600">Question {currentQuestion + 1} of 32</span>
      </div>

      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">{UricaQuestions[currentQuestion]}</h2>

        <div className="space-y-4">
          {[1, 2, 3, 4, 5].map((value) => (
            <label 
              key={value} 
              className={`flex items-center space-x-3 p-3 border rounded-lg cursor-pointer transition-colors
                ${answers[currentQuestion] === value ? 'bg-blue-50 border-blue-500' : 'hover:bg-gray-50'}`}
            >
              <input
                type="radio"
                name={`question-${currentQuestion}`}
                value={value}
                checked={answers[currentQuestion] === value}
                onChange={() => handleAnswer(value)}
                className="h-4 w-4 text-blue-600"
              />
              <span className="text-lg">{value}</span>
            </label>
          ))}
        </div>
      </div>

      <div className="flex justify-between items-center">
        <button
          onClick={() => setCurrentQuestion(Math.max(0, currentQuestion - 1))}
          disabled={currentQuestion === 0}
          className="px-4 py-2 bg-gray-200 rounded-lg disabled:opacity-50 hover:bg-gray-300 transition-colors"
        >
          Previous
        </button>

        <div className="flex items-center space-x-4">
          <span className="text-gray-600">
            {answers.filter(a => a !== null).length} of 32 questions answered
          </span>

          <button
            onClick={handleNext}
            disabled={answers[currentQuestion] === null}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg disabled:opacity-50 hover:bg-blue-700 transition-colors"
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default Questionnaire;
